commits.content:
html,
body {
  width: 100%;
  height: 100%;
  background-color: #eaeded;
  color: #444444;
  overflow-x: hidden;
  box-sizing: border-box;
}

.navbar {
  background-color: #232f3e !important;
  padding: 4px;
}

.navbar-brand img {
  width: 100px;
  margin-left: 12px;
  border-radius: 7px;
  margin-right: 15px;
}

.navbar-brand {
  color: #7c795d;
  font-family: 'Trocchi', serif;
  font-size: 45px;
  font-weight: normal;
  line-height: 48px;
  margin: 0;
}

.navbar-nav li {
  padding-right: 45px;
  font-size: 15px;
}

.nav-link::after {
  content: '';
  width: 0;
  display: block;
  height: 3px;
  background: #e0a800;
  transition: width 0.3s;
}
.nav-link:hover::after {
  width: 100%;
  transition: width 0.3s;
}

.padding {
  padding-bottom: 45px;
}

.carousel-inner img {
  width: 640px;
  height: 360px;
}
.carousel-caption {
  position: absolute;
  /* top: 20%; */

  /* transform: translateY(-30%); */
}
.carousel-caption h3 h1 p {
  text-shadow: 0 0 5px #ffffff;
}

.top-left {
  position: absolute;
  top: 8px;
  left: 16px;
}
